mapper.select:
SELECT RNUM , E.LEC_NO , E.LEC_PAY , E.LEC_STATUS , E.ACC_STATUS , E.ACC_DATE , E.ACC_OPTION , E.MEMBER_ID , E.MEMBER_NAME , E.CLS_ID , E.CLS_NAME , E.CLS_PAY , E.CLS_START , E.CLS_END , E.REF_NO , E.REF_PAY , E.REF_DATE , E.REF_CONTENT FROM (SELECT ROWNUM AS RNUM , D.LEC_NO , D.LEC_PAY , D.LEC_STATUS , D.ACC_STATUS , D.ACC_DATE , D.ACC_OPTION , D.MEMBER_ID , D.MEMBER_NAME , D.CLS_ID , D.CLS_NAME , D.CLS_PAY , D.CLS_START , D.CLS_END , D.REF_NO , D.REF_PAY , D.REF_DATE , D.REF_CONTENT FROM (SELECT A.LEC_NO , A.LEC_PAY , A.LEC_STATUS , A.ACC_STATUS , A.ACC_DATE , A.ACC_OPTION , A.MEMBER_ID , B.MEMBER_NAME , C.CLS_ID , C.CLS_NAME , C.CLS_PAY , C.CLS_START , C.CLS_END , F.REF_NO , F.REF_PAY , F.REF_DATE , F.REF_CONTENT FROM TB_LECTURE A JOIN TB_MEMBER B ON(A.MEMBER_ID = B.MEMBER_ID) JOIN TB_CLASS C ON(A.CLS_ID = C.CLS_ID) LEFT JOIN TB_REFUND F ON(A.LEC_NO = F.LEC_NO) WHERE A.LEC_DELETE = 'N' AND A.MEMBER_ID = #{ memberId }
AND UPPER(C.CLS_NAME) LIKE UPPER('%' || #{ keyword } || '%')
AND A.LEC_STATUS LIKE '%' || #{ keyword } || '%'
AND A.ACC_OPTION LIKE '%' || #{ keyword } || '%'
AND A.ACC_STATUS LIKE '%' || #{ keyword } || '%'
ORDER BY A.LEC_NO DESC ) D
WHERE ROWNUM <= #{ pageNo } * #{ limit }
) E
WHERE RNUM > (#{ pageNo } - 1) * #{ limit }
